package conditional;

public class EX2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 10;
		
		if (age >= 8) { //나이가 8살 이상이면 출력
			System.out.println("학교에 다닙니다."); //조건이 맞으면 "학교에 다닙니다." 출력
		} else {
			System.out.println("학교에 다니지않습니다."); // 조건에 맞지 않으면 "학교에 다니지않습니다." 출력
		}

	}

}
